contracts/interfaces/IInvestor.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

/**
 * @title IInvestor
 * @notice This interface defines the functions to add and remove opportunities by the investor
 */
interface IInvestor {
    function addOpportunity(address _investor, bytes32 _opportunityId) external;

    function removeOpportunity(
        address _investor,
        bytes32 _opportunityId
    ) external;

    function getOpportunities(
        address _investor
    ) external view returns (bytes32[] memory);

    function getInvestor(
        address _investor,
        bytes32 _opportunityId
    ) external view returns (bool);
}

contracts/interfaces/IOpportunityManager.content:
//SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

/**
 * @title IOpportunityManager
 * @author
 * @notice This interface defines the functions for the Opportunity Manager
 */
interface IOpportunityManager {
    enum OpportunityStatus {
        Active,
        Rejected,
        Collateralized,
        DrawnDown,
        Repaid,
        UnderReview,
        WriteOff,
        Approved,
        Unsure
    }

    enum LoanType {
        BulletPaymentLoan,
        ArmotizedLoan
    }

    struct Opportunity {
        bytes32 opportunityId;
        address borrower;
        string opportunityName;
        string opportunityDescription;
        LoanType loanType;
        uint256 loanAmount;
        uint256 loanTermInDays;
        uint256 loanInterest;
        uint256 paymentFrequencyInDays;
        string collateralDocument;
        uint256 InvestmentLoss;
        OpportunityStatus opportunityStatus;
        address opportunityPoolAddress;
        uint256 createdAt;
    }

    struct CreateOpportunity {
        address borrower;
        string opportunityName;
        string opportunityDescription;
        LoanType loanType;
        uint256 loanAmount;
        uint256 loanTermInDays;
        uint256 loanInterest;
        uint256 paymentFrequencyInDays;
        string collateralDocument;
        uint256 InvestmentLoss;
    }

    function writeOffDaysOf(
        bytes32 _opportunityId
    ) external view returns (uint256);

    function getTotalOpportunities() external view returns (uint256);

    function getOpportunityOf(
        address _borrower
