// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

library ConfigOptions {
    // NEVER EVER CHANGE THE ORDER OF THESE!
    // You can rename or append. But NEVER change the order.
    enum Addresses {
        ReignAdmin,
        ReignCoin,
        USDCToken,
        SeniorPool,
        PoolImplAddress,
        CollateralToken,
        OpportunityManager,
        InvestorContract,
        ReignTreasury,
        ReignKeeper,
        IdentityToken
    }

    enum Numbers {
        LeverageRatio,
        ReignFee,
        OverDueFee,
        JuniorSubpoolFee,
        SeniorPoolFundLockinMonths,
        WriteOffDays,
        AdjustmentOffset
    }
}

contracts/protocol/Constants.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

library Constants {
    uint256 public constant ONE_YEAR_IN_DAYS = 365;
    uint256 public constant ONE_MONTH_IN_DAYS = 30;
    uint256 public constant SIX_DECIMALS = 1e6;
    uint256 public constant ONE_DAY = 86400;
    uint256 public constant ONE_MONTH = ONE_DAY * 30;
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant SENIOR_POOL_ROLE = keccak256("SENIOR_POOL_ROLE");
    bytes32 public constant BORROWER_ROLE = keccak256("BORROWER_ROLE");
    bytes32 public constant POOL_LOCKER_ROLE = keccak256("POOL_LOCKER_ROLE");
    bytes32 public constant AUTHORIZE_USER_ROLE =
        keccak256("AUTHORIZE_USER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant OWNER = keccak256("OWNER");

    function oneYearInDays() internal pure returns (uint256) {
        return ONE_YEAR_IN_DAYS;
    }

    function oneMonthInDays() internal pure returns (uint256) {
        return ONE_MONTH_IN_DAYS;
    }

    function sixDecimals() internal pure returns (uint256) {
        return SIX_DECIMALS;
    }

    function getSeniorPoolRole() internal pure returns (bytes32) {
        return SENIOR_POOL_ROLE;
    }

    function getBorrowerRole() internal pure returns (bytes32) {
        return BORROWER_ROLE;
    }

    function getPoolLockerRole() internal pure returns (bytes32) {
        return POOL_LOCKER_ROLE;
    }

    function getAuthorizeUserRole() internal pure returns (bytes32) {
        return AUTHORIZE_USER_ROLE;
    }

    function getPauserRole() internal pure returns (bytes32) {
        return PAUSER_ROLE;
    }

    function getMinterRole() internal pure returns (bytes32) {
        return MINTER_ROLE;
    }

    function getUpgraderRole() internal pure returns (bytes32) {
        return UPGRADER_ROLE;
    }

    function getOwnerRole() internal pure returns (bytes32) {
        return OWNER;
    }

    function getAdminRole() internal pure returns (bytes32) {
        return ADMIN_ROLE;
    }

    function oneDay() internal pure returns (uint256) {
        return ONE_DAY;
    }

    function oneMonth() internal pure returns (uint256) {
        return ONE_MONTH;
    }
}

contracts/protocol/MultiSign.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import {ReignTreasury} from "./ReignTreasury.sol";

contract multiSigWallet {
    event Deposit(address indexed sender, uint256 amount, uint256 balance);
    event SubmitTransaction(
        address indexed owner,
        uint256 indexed txIndex,
        address indexed to,
        uint256 value
    );

    event ConfirmTransaction(address indexed owner, uint256 indexed txIndex);
    event RevokeConfirmation(address indexed owner, uint256 indexed txIndex);
    event ExecuteTransaction(address indexed owner, uint256 indexed txIndex);

    address[] public owners;
    mapping(address => bool) public isOwner;
    uint256 public numConfirmationsRequired;
    address reignTreasuryWallet;

    struct Transaction {
        address to;
        uint256 value;
        bool executed;
        uint256 numConfirmations;
    }

    // mapping from tx index => owner => bool
    mapping(uint256 => mapping(address => bool)) public isConfirmed;

    Transaction[] public transactions;

    modifier onlyOwner() {
        require(isOwner[tx.origin], "not owner");
        _;
    }

    modifier txExists(uint256 _txIndex) {
        require(_txIndex < transactions.length, "tx does not exist");
        _;
    }

    modifier notExecuted(uint256 _txIndex) {
        require(!transactions[_txIndex].executed, "tx already executed");
        _;
    }

    modifier notConfirmed(uint256 _txIndex) {
        require(!isConfirmed[_txIndex][tx.origin], "tx already confirmed");
        _;
    }

    constructor(
        address[] memory _owners,
        uint256 _numConfirmationsRequired,
        address _reignTreasuryWallet
    ) {
        require(_owners.length > 0, "owners required");
        require(
            _numConfirmationsRequired > 0 &&
                _numConfirmationsRequired <= _owners.length,
            "invalid number of required confirmations"
        );

        for (uint256 i = 0; i < _owners.length; i++) {
            address owner = _owners[i];

            require(owner != address(0), "invalid owner");
            require(!isOwner[owner], "owner not unique");

            isOwner[owner] = true;
            owners.push(owner);
        }

        numConfirmationsRequired = _numConfirmationsRequired;
        reignTreasuryWallet = _reignTreasuryWallet;
    }

    function submitTransaction(address _to, uint256 _value) public onlyOwner {
        uint256 txIndex = transactions.length;

        transactions.push(
            Transaction({
                to: _to,
                value: _value,
                executed: false,
                numConfirmations: 0
            })
        );

        emit SubmitTransaction(tx.origin, txIndex, _to, _value);
    }

    function confirmTransaction(
        uint256 _txIndex
    )
        public
        onlyOwner
        txExists(_txIndex)
        notExecuted(_txIndex)
        notConfirmed(_txIndex)
    {
        Transaction storage transaction = transactions[_txIndex];
        transaction.numConfirmations += 1;
        isConfirmed[_txIndex][tx.origin] = true;

        emit ConfirmTransaction(tx.origin, _txIndex);
        if (transaction.numConfirmations >= numConfirmationsRequired) {
            executeTransaction(_txIndex);
        }
    }

    function executeTransaction(
        uint256 _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];
        require(
            transaction.numConfirmations >= numConfirmationsRequired,
            "cannot execute tx"
        );

        transaction.executed = true;
        ReignTreasury(reignTreasuryWallet).withdraw(
            transaction.to,
            transaction.value
        );

        emit ExecuteTransaction(tx.origin, _txIndex);
    }

    function setTreasuryWallet(address _reignTreasuryWallet) public onlyOwner {
        reignTreasuryWallet = _reignTreasuryWallet;
    }

    function revokeConfirmation(
        uint256 _txIndex
    ) public onlyOwner txExists(_txIndex) notExecuted(_txIndex) {
        Transaction storage transaction = transactions[_txIndex];
        require(isConfirmed[_txIndex][tx.origin], "tx not confirmed");

        transaction.numConfirmations -= 1;
        isConfirmed[_txIndex][tx.origin] = false;

        emit RevokeConfirmation(tx.origin, _txIndex);
    }

    function getOwners() public view returns (address[] memory) {
        return owners;
    }

    function getTransactionCount() public view returns (uint256) {
        return transactions.length;
    }

    function getTransaction(
        uint256 _txIndex
    )
        public
        view
        returns (
            address to,
            uint256 value,
            bool executed,
            uint256 numConfirmations
        )
    {
        Transaction storage transaction = transactions[_txIndex];

        return (
            transaction.to,
            transaction.value,
            transaction.executed,
            transaction.numConfirmations
        );
    }
}

