//SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {Constants} from "./Constants.sol";

contract PauserPausable is AccessControlUpgradeable, PausableUpgradeable {
    modifier onlyPauserRole() {
        require(
            hasRole(Constants.getPauserRole(), _msgSender()),
            "PauserPausable: Caller is not a pauser"
        );
        _;
    }

    function __PauserPausable__init() public initializer {
        __Pausable_init_unchained();
    }

    /**
     * @dev To pause all functions guarded by Pause
     *
     * Requirements:
     *
     * - The caller must have the `PAUSER_ROLE`.
     */

    function pause() public onlyPauserRole {
        _pause();
    }

    function unpause() public onlyPauserRole {
        _unpause();
    }
}

contracts/protocol/ReignConfig.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import {BaseUpgradeablePausable} from "./BaseUpgradeablePausable.sol";
import {ConfigHelper} from "./ConfigHelper.sol";

contract ReignConfig is BaseUpgradeablePausable {
    mapping(uint256 => address) public addresses;
    mapping(uint256 => uint256) public numbers;
    mapping(bytes32 => bool) public flag;

    using ConfigHelper for ReignConfig;

    function initialize() public initializer {
        require(msg.sender != address(0), "Invalid Sender Address");
        _BaseUpgradeablePausable_init(msg.sender);
        setAddress(0, msg.sender);
    }

    function setAddress(
        uint256 indexAddress,
        address newAddress
    ) public onlyAdmin nonReentrant {
        require(address(newAddress) != address(0), "Invalid Address");
        addresses[indexAddress] = newAddress;
    }

    function getAddress(uint256 indexAddress) public view returns (address) {
        return addresses[indexAddress];
    }

    function setNumber(
        uint256 id,
        uint256 newNumber
    ) public onlyAdmin nonReentrant {
        numbers[id] = newNumber;
    }

    function getNumber(uint256 id) public view returns (uint256) {
        return numbers[id];
    }

    function setFlag(
        bytes32 indexFlag,
        bool isFlag
    ) public onlyAdmin nonReentrant {
        flag[indexFlag] = isFlag;
    }

    function getFlag(bytes32 indexFlag) public view returns (bool) {
        return flag[indexFlag];
    }
}

contracts/protocol/ReignTreasury.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import {BaseUpgradeablePausable} from "./BaseUpgradeablePausable.sol";
import {ConfigHelper} from "./ConfigHelper.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {multiSigWallet} from "./MultiSign.sol";
import {ReignConfig} from "./ReignConfig.sol";

contract ReignTreasury is BaseUpgradeablePausable {
    using SafeERC20 for IERC20;
    using ConfigHelper for ReignConfig;

    ReignConfig public reignConfig;
    IERC20 public usdcToken;
    address wallet;

    function setWallet(address _wallet) public onlyAdmin {
        wallet = _wallet;
    }



    function initialize(ReignConfig _reignConfig) external initializer {
        require(address(_reignConfig) != address(0), "Invalid Address");
        reignConfig = _reignConfig;
        address owner = reignConfig.reignAdminAddress();
        require(owner != address(0), "Invalid Owner Address");
        _BaseUpgradeablePausable_init(owner);
        usdcToken = IERC20(reignConfig.usdcAddress());
    }

    function getTreasuryBalance() public view returns (uint256) {
        uint256 balance = usdcToken.balanceOf(address(this));
        return balance;
    }

    function withdraw(
        address to,
        uint256 amount
    ) public nonReentrant whenNotPaused onlyAdmin {
        require(amount > 0, "Amount Should be greater than zero");
        require(msg.sender == wallet, "Only wallet can withdraw");
        uint256 totalBalance = getTreasuryBalance();
        require(totalBalance >= amount, "Insufficient Balance");
        usdcToken.safeTransferFrom(address(this), to, amount);
    }
}

